import React, {useState} from 'react';
import CardView from "./小ardView/小ardView";
import './App.css';
import './cards.css';
import CardDeck from "./lib/CardDeck";
import Card from "./lib/Card";


const App = () => {
  const [cards, setCards] = useState<Card[]>([]);

  const getCards = () => {
    const cardDeck = new CardDeck();
    const newCards = cardDeck.getCards(5);
    setCards(newCards);

    if (cards.length === 0) {
      return <button onClick={cardDeck.getCards(5)}>Deal cards</button>;
    }

  };

  return (
    <div className="App">
      <CardView rank="K" suit="diams"/>
      <div>
        <button onClick={getCards}>Deal cards</button>
      </div>
    </div>
  );
};



const cardDeck = new CardDeck();
console.log(cardDeck.deck);
cardDeck.getCard();
console.log(cardDeck.getCard());
cardDeck.getCards(5);
console.log(cardDeck.getCards(5));

export default App;
